mapper.select:
SELECT facility_no facilityNo, category_cd categoryCd, facility_name facilityName, facility_note facilityNote, use_yn useYn, reg_dtime regDtime, mod_dtime modDtime, reg_id regId, mod_id modId FROM facility_base
1 = 1
AND use_yn = #{useYn}
AND category_cd LIKE #{categoryCd}
ORDER BY category_cd ASC

SELECT COUNT(*) cnt
FROM facility_base

	facility_name = #{facilityName}

		AND facility_no != #{facilityNo}

SELECT grp_cd grpCd, cd, cd_nm cdNm, use_yn useYn, reg_dtime regDtime, mod_dtime modDtime, reg_id regId, mod_id modId FROM st_std_cd
grp_cd = 'F001'
AND cd LIKE #{cd}

SELECT COUNT(*) cnt
FROM facility_base
WHERE category_cd = #{cd}

mapper.update:
UPDATE st_std_cd 
SET use_yn = #{useYn}
WHERE grp_cd = 'F001'
AND cd = #{cd};
